$url = "https://open.faceit.com/data/v4/championships?game=csgo&offset=0&limit=$champAmount";
$ch = curl_init();
//  Initiate curl
// Will return the response, if false it print the response
curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
// Set the url
curl_setopt($ch, CURLOPT_URL,$url);
// SET AUTH
curl_setopt($ch, CURLOPT_HTTPHEADER, array('Content-Type: application/json' , $authorization ));
// Execute
$result = curl_exec($ch);
$tournamentArray = json_decode($result, true);

$organizers = [
    "id" => [],
    "organizer_name" => [],
    "organizer_picture" => []
];

$length = count($tournamentArray['items']);

for ($i=0; $i < $length; $i++) { 
    array_push($organizers['id'], $tournamentArray['items'][$i]['organizer_id']);
    $organizersurl = "https://open.faceit.com/data/v4/organizers/".$tournamentArray['items'][$i]['organizer_id']."";
    curl_setopt($ch, CURLOPT_URL,$organizersurl);
    $resultOrg = curl_exec($ch);
    $organizerArray = json_decode($resultOrg, true);
    array_push($organizers['organizer_name'], $organizerArray['name']);
    array_push($organizers['organizer_picture'], $organizerArray['avatar']);
}
curl_close($ch);
}
